axu4j.buttonTag:

{{#isButton}}<button type="button" id="{{id}}" class="AXButton {{css}}" style={{style}}>{{text}}</button>{{/isButton}}
{{#isAnchor}}<a href="#{{id}}" id="{{id}}" class="AXButton {{css}}" style={{style}}>{{text}}</a>{{/isAnchor}}
{{#isInput}}<input type="button" id="{{id}}" value="{{text}}" class="AXButton {{css}}" style={{style}} />{{/isInput}}

axu4j.inputTag:

{{#isText}}<input type="text" id="{{id}}" name="{{name}}" value="{{value}}" class="AXInput {{css}}" placeholder="{{placeHolder}}" style={{style}} />{{/isText}}
{{#isNumber}}<input type="tel" id="{{id}}" name="{{name}}" value="{{value}}" class="AXInput ax-number {{css}}" placeholder="{{placeHolder}}" style={{style}} />{{/isNumber}}
{{#isSwitch}}{{#label}}<label class="AXInputLabel">{{label}}</label>{{/label}}<input type="text" id="{{id}}" name="{{name}}" value="{{value}}" style={{style}} />{{/isSwitch}}
